    ev, err := json.Unmarshal(event)
    if err != nil {
        g.logger.error("failed to unmarshal event %s", event)
        return
    }

    // add in a normalized way for the read model to easily query
    err := g.queryStore.AddUserToGroup(ev.aggregate_id, ev.payload)
}

